def calculate_stability(memory: MemoryNode) -> float:
    """How long this type of memory typically lasts"""
    base_stability = {
        'semantic': 365,    # facts last ~1 year base
        'procedural': 180,  # procedures last ~6 months
        'episodic': 90,     # events last ~3 months
        'emotional': 60     # emotions fade in ~2 months
    }[memory.type]

    # Adjust based on importance
    return base_stability * (1 + memory.importance_score)
