class MemoryQualityControl:
    def __init__(self):
        self.spam_detector = SpamDetector()
        self.hallucination_detector = HallucinationDetector()
        self.quality_threshold = 0.6

    async def validate_memory_before_storage(self, raw_memory: str, context: dict) -> ValidationResult:
        """Prevent bad data from polluting the memory system"""

        # 1. Spam detection - repetitive or low-value content
        if await self.spam_detector.is_spam(raw_memory):
            return ValidationResult(valid=False, reason="spam_detected")

        # 2. Hallucination detection - impossible or inconsistent facts
        if await self.hallucination_detector.is_hallucination(raw_memory, context):
            return ValidationResult(valid=False, reason="hallucination_detected")

        # 3. Redundancy check - is this substantially new information?
        similar_memories = await self.find_similar_existing_memories(raw_memory)
        if self.is_redundant(raw_memory, similar_memories):
            return ValidationResult(valid=False, reason="redundant_information")

        # 4. Coherence check - does this fit with existing knowledge?
        coherence_score = await self.calculate_coherence(raw_memory)
        if coherence_score < self.quality_threshold:
            return ValidationResult(valid=False, reason="low_coherence", score=coherence_score)

        return ValidationResult(valid=True, quality_score=coherence_score)
